import { all, fork, takeLatest, takeEvery, call, put, take } from 'redux-saga/effects'
import { LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE, SIGN_UP_REQUEST } from '../reducers/user'

function loginAPI() {
    // 서버에 요청
}

function signUpAPI() {

}

function* login() {
    try {
        yield call(loginAPI)
        yield put({ // dispatch와 같은역할
            type: LOG_IN_SUCCESS
        })
    } catch(e) {
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* signUp() {
    try {
        yield call(signUpAPI)
        yield put({
            type: LOG_IN_SUCCESS,
        })
    } catch(e) {
        console.error(e)
        yield put({
            type: LOG_IN_FAILURE,
        })
    }
}

function* watchLogin() {
    yield takeLatest(LOG_IN_REQUEST, login)
}

function* watchSignUp() {
    yield takeEvery(SIGN_UP_REQUEST, signUp)
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchSignUp)
    ])
}
