import { all, fork, takeLatest, takeEvery, call, put, take } from 'redux-saga/effects'
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE } from '../reducers/user'

const HELLO_SAGA = 'HELLO_SAGA'

function loginAPI() {
    // 서버에 요청
}

function* login() {
    try {
        yield call(loginAPI)
        yield put({ // dispatch와 같은역할
            type: LOG_IN_SUCCESS
        })
    } catch(e) {
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* watchSignUp() {

}

function* watchLogin() {
    while(true) {
        yield take(LOG_IN)
        yield delay(2000)
        yield put({
            type: LOG_IN_SUCCESS,
        })
    }
}

function* watchHello() {
    yield takeEvery(HELLO_SAGA, function*() {
        console.log(1)
        console.log(2)
        console.log(3)
        console.log(4)
    })
}

export default function* userSaga() {
    yield all([
        watchLogin(),
        watchSignUp(),
        watchHello9
    ])
}
