import { all, fork, takeLatest } from 'redux-saga/effects'
import { ADD_POST_REQUSET } from '../reducers/post'

function* watchAddPost() {
    yield takeLatest(ADD_POST_REQUSET, addPost)
}

export default function* postSaga() {
    yield all([
        fork(watchAddPost)
    ])
}
