import React from 'react'
import PostForm from '../components/PostForm'
import PostCard from '../components/PostCard'

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [
        { User: {
            id: 1,
            nickname: '삼바림'
        },
        content: '첫 번째 게시물',
        img: 'https://i.imgur.com/DxH71p6.png'
    }]
}

const Home = () => {
    return (
        <div>
            {dummy.isLoggedIn && <PostForm />}
            {dummy.mainPosts.map((c) => {
                return(
                    <PostCard />
                )
            })}
        </div>
    )
}

export default Home
